mapper.select:
SELECT ar.*,
u.user_name,
u.user_avatar
FROM attraction_rating ar
LEFT JOIN user u ON ar.user_id = u.id


        AND ar.id = #{id}


        AND ar.attraction_id = #{attractionId}


        AND ar.user_id = #{userId}


        AND sl.create_time BETWEEN #{startTime} AND #{endTime}


ORDER BY ar.grade

    LIMIT #{current},#{size}

SELECT COUNT(*)
FROM attraction_rating ar


        AND ar.id = #{id}


        AND ar.attraction_id = #{attractionId}


        AND ar.user_id = #{userId}


        AND sl.create_time BETWEEN #{startTime} AND #{endTime}
