mapper.select:
SELECT ast.*,u.user_name,u.user_avatar,a.name AS attraction_name
FROM attraction_strategy ast
LEFT JOIN attractions a ON ast.attraction_id = a.id
LEFT JOIN user u ON ast.user_id = u.id


        AND ast.id = #{id}


        AND ast.user_id = #{userId}


        AND ast.attraction_id = #{attractionId}


        AND ast.title LIKE concat('%',#{title},'%')


        AND ast.is_audit = #{isAudit}


        AND ast.create_time BETWEEN #{startTime} AND #{endTime}


ORDER BY ast.id DESC

    LIMIT #{current},#{size}

SELECT COUNT(*)
FROM attraction_strategy ast


        AND ast.id = #{id}


        AND ast.user_id = #{userId}


        AND ast.attraction_id = #{attractionId}


        AND ast.title LIKE concat('%',#{title},'%')


        AND ast.is_audit = #{isAudit}


        AND ast.create_time BETWEEN #{startTime} AND #{endTime}
