mapper.select:
SELECT at.*,
s.name AS attraction_name
FROM attraction_ticket at
LEFT JOIN attractions s ON at.attraction_id = s.id


        AND at.id = #{id}


        AND at.attraction_id = #{attractionId}


        AND at.description LIKE concat('%',#{description},'%')


        AND at.ticket_status = #{ticketStatus}


        AND at.create_time BETWEEN #{startTime} AND #{endTime}


ORDER BY at.id DESC

    LIMIT #{current},#{size}

SELECT COUNT(*)
FROM attraction_ticket at


        AND at.id = #{id}


        AND at.attraction_id = #{attractionId}


        AND at.create_time BETWEEN #{startTime} AND #{endTime}
