SELECT sto.*,
u.user_name,
s.id AS attraction_id,
s.name AS attraction_name,
st.description,
st.number,
st.price,
st.discount
FROM attraction_ticket_order sto
LEFT JOIN user u ON sto.user_id = u.id
LEFT JOIN attraction_ticket st ON sto.ticket_id = st.id
LEFT JOIN attractions s ON st.attraction_id = s.id


        AND sto.id = #{id}


        AND sto.user_id = #{userId}


        AND sto.ticket_id = #{ticketId}


        AND sto.attraction_id = #{attractionId}


        AND sto.pay_status = #{payStatus}


        AND sto.create_time BETWEEN #{startTime} AND #{endTime}


ORDER BY sto.id DESC

    LIMIT #{current},#{size}

