SELECT sto.*, u.user_name, s.id AS attraction_id, s.name AS attraction_name, st.description, st.number, st.price, st.discount FROM attraction_ticket_order sto LEFT JOIN user u ON sto.user_id = u.id LEFT JOIN attraction_ticket st ON sto.ticket_id = st.id LEFT JOIN attractions s ON st.attraction_id = s.id WHERE sto.ticket_id IN
#{id}
AND sto.pay_status = #{attractionTicketOrderQueryDto.payStatus}
AND sto.create_time BETWEEN #{attractionTicketOrderQueryDto.startTime} AND #{attractionTicketOrderQueryDto.endTime}
AND sto.user_id = #{attractionTicketOrderQueryDto.userId}
ORDER BY sto.id DESC
LIMIT #{attractionTicketOrderQueryDto.current}, #{attractionTicketOrderQueryDto.size}
